<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_alarms extends Fsadfzerrwez_home{		function Fsadfzerrwez_history(){					parent::__construct();				$this->load->model("alarm");						}				/***********************************************************************/		/**************************** START ALARMS *******************************/				/***********************************************************************/				function alarms_info(){			$this->check_login();						$this->check_role(MODULE_ALARMS, MODULE_TYPE_VIEW);			$this->load->model("alarm");			$company_id = get_company_id();			$data = $this->alarm->get_alarms_info($company_id);			echo json_encode($data);		}				/***********************************************************************/		/**************************** END OF alarms_history *******************************/				/***********************************************************************/			}?>