<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_alarms_history extends Fsadfzerrwez_home{		function Fsadfzerrwez_alarms_history(){					parent::__construct();				$this->load->model("alarm_history");			$this->load->model("device");						}				/***********************************************************************/		/**************************** START alarms_history *******************************/				/***********************************************************************/				function alarms_history(){			$this->check_login();						$this->check_role(MODULE_ALARMS, MODULE_TYPE_VIEW);			$data = $this->get_alarms_history();			$this->load_page('alarms-history', $this->lang->line('alarms-history'), $data);		}				function ajax_alarms_history(){						$this->check_login_ajax();			$this->check_role_ajax(MODULE_ALARMS, MODULE_TYPE_VIEW);			$data = $this->get_alarms_history();			$model['data'] = $data;			$model['controller_name'] = $this->controller_name;			$this->load->view($this->home_directory . "alarms-history-table", $model);		}				private function get_alarms_history(){			$data = $this->get_page_config();				$sort_by = $this->input->get('sort_by');			$ascending = $this->input->get('ascending');			$company_id = get_company_id();			$query = $this->alarm_history->get_alarms_history($data['keywords'], $company_id, $data['page'], $data['page_size'], $sort_by, $ascending, $num_rows, get_language());			$this->set_paging_data($data, $query, $num_rows);									return $data;		}				function delete_alarms_history(){			$this->check_login_ajax();			$this->check_role_ajax(MODULE_ALARMS, MODULE_TYPE_DELETE);			$ids = $this->get_array_ids();			$this->alarm_history->delete_in($ids, get_company_id());		}		/***********************************************************************/		/**************************** END OF alarms_history *******************************/				/***********************************************************************/			}?>