<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_device_types extends Fsadfzerrwez_home{		function Fsadfzerrwez_device_types(){					parent::__construct();				$this->load->model("device_type");					}				/***********************************************************************/		/**************************** START DEVICE TYPES *******************************/				/***********************************************************************/				function device_types(){			$this->check_login();						$this->check_role(MODULE_DEVICE_TYPES, MODULE_TYPE_VIEW);			$data = $this->get_device_types();			$this->load_page('device-types', $this->lang->line('device-types'), $data);		}				function ajax_device_types(){						$this->check_login_ajax();			$this->check_role_ajax(MODULE_DEVICE_TYPES, MODULE_TYPE_VIEW);			$data = $this->get_device_types();			$model['data'] = $data;			$model['controller_name'] = $this->controller_name;			$this->load->view($this->home_directory . "device-types-table", $model);		}				private function get_device_types(){			$data = $this->get_page_config();				$sort_by = $this->input->get('sort_by');			$ascending = $this->input->get('ascending');			$company_id = get_company_id();			$query = $this->device_type->get_device_types($data['keywords'], $company_id, $data['page'], $data['page_size'], $sort_by, $ascending, $num_rows, get_language());			$this->set_paging_data($data, $query, $num_rows);									return $data;		}				function add_device_type($id = null){										$this->check_login();			$this->check_role(MODULE_DEVICE_TYPES, MODULE_TYPE_VIEW);			$result = null;			$device_type = null;				$err_msg = '';			$company_id = get_company_id();			$data = array();
			$data['back-url'] = (base_url("devices-types"), MODULE_DEVICE_TYPES);			if (is_post()){									$result = false;				$device_type = $this->device_type->get_post_data();								$this->form_validation->set_rules('type_name', $this->lang->line('required-type-name'), 'trim|required');				if ($this->form_validation->run() == TRUE){					$device_type->company_id = $company_id;					//UPDATE CASE						if ($id){						$this->check_role(MODULE_DEVICE_TYPES, MODULE_TYPE_UPDATE);												$result = $this->device_type->update($device_type, $company_id, $id);						if ($result){							$err_msg = get_saved_message('device-type', $device_type->type_name);							if ($this->input->post('save_and_continue') == false){																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}							else{							$err_msg = get_saved_fail_message('device-type', $device_type->type_name);						}					}					else{						$this->check_role(MODULE_DEVICE_TYPES, MODULE_TYPE_ADD);												$result = $this->device_type->insert($device_type, get_user_id());												if ($result){								$err_msg = get_saved_message('device-type', $device_type->type_name);							if ($this->input->post('save_and_continue') == true){								//clear old data								$device_type->clear_data();							}							else{																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}						else{							$err_msg = get_saved_fail_message('device-type', $device_type->type_name);						}											}				}				else{					$err_msg = $this->lang->line('required-device-type');				}			}			else{								if ($id){										$device_type = $this->device_type->get_id($id);									}				else{										$device_type = new device_type();				}			}			$data['err_msg'] = $err_msg;			$data['result'] = $result;									$data['data'] = $device_type;			$data['id'] = $id;						$data['page'] = $this->input->get('page');			$data['page_size'] = $this->input->get('page_size');			$data['keywords'] = $this->input->get('keywords');			$title_page = $this->lang->line('device-type');			if (!$device_type){				$title_page = get_not_found_short();			}			$this->load_page('add-device-type', $title_page, $data);		}				function active_device_types(){						$this->check_login_ajax();						$this->check_role_ajax(MODULE_DEVICE_TYPES, MODULE_TYPE_UPDATE);			$active = $this->input->post('active');						$ids = $this->get_array_ids();									$this->device_type->active_in(get_company_id(), $active, $ids);		}				function delete_device_types(){			$this->check_login_ajax();			$this->check_role_ajax(MODULE_DEVICE_TYPES, MODULE_TYPE_DELETE);			$ids = $this->get_array_ids();			$this->device_type->delete_in(get_company_id(), $ids);		}		/***********************************************************************/		/**************************** END OF DEVICE TYPES *******************************/				/***********************************************************************/			}?>