<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_email_config extends Fsadfzerrwez_home{		function Fsadfzerrwez_email_config(){					parent::__construct();				$this->load->model("email_config");					}				/***********************************************************************/		/**************************** START EMAIL CONFIG *******************************/				/***********************************************************************/				function config(){						$this->check_login();			$this->check_role(MODULE_EMAIL_CONFIG, MODULE_TYPE_VIEW);			$result = null;			$type = null;				$err_msg = '';			$data = array();			$email_config = null;						if (is_post()){				$this->check_role(MODULE_EMAIL_CONFIG, MODULE_TYPE_ADD);				$this->check_role(MODULE_EMAIL_CONFIG, MODULE_TYPE_UPDATE);				$result = false;				$email_config = $this->email_config->get_post_data();				$result = $this->email_config->insert_or_update($email_config, get_user_id());				if ($result){					$err_msg = get_saved_message('', $this->lang->line('email-config'));											}					else{					$err_msg = get_saved_fail_message('email-config', $this->lang->line('email-config'));				}							}			else{								$email_config = $this->email_config->get_one();				if (!$email_config){					$email_config = new email_config();				}			}								$data['err_msg'] = $err_msg;			$data['result'] = $result;									$data['data'] = $email_config;			$this->load_page('email-config', $this->lang->line('email-config'), $data);		}				/***********************************************************************/		/**************************** END OF EMAIL CONFIG *******************************/				/***********************************************************************/			}?>