<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_event_logs extends Fsadfzerrwez_home{		function Fsadfzerrwez_event_logs(){					parent::__construct();				$this->load->model("event_log");			$this->load->model("device");		}				/***********************************************************************/		/**************************** START EVENT LOGS *******************************/				/***********************************************************************/				function logs($id){						$this->check_login();			$this->check_role(MODULE_DEVICES, MODULE_TYPE_VIEW);			$data = $this->get_logs($id);			$company_id = get_company_id();			$data['device'] = $this->device->get_id($id, $company_id);			$this->load_page('event-logs', $this->lang->line('event-logs'), $data);		}				function ajax_logs($id){						$this->check_login_ajax();			$this->check_role_ajax(MODULE_DEVICES, MODULE_TYPE_VIEW);			$data = $this->get_logs($id);			$model['data'] = $data;			$model['controller_name'] = $this->controller_name;			$this->load->view($this->home_directory . "event-logs-table", $model);		}				private function get_logs($device_id){												$data = $this->get_page_config();			$sort_by = $this->input->get('sort_by');			$ascending = $this->input->get('ascending');			$company_id = get_company_id();			$num_rows = 0;			$query = $this->event_log->get_device_logs($device_id, $company_id, $data['page'], $data['page_size'], $num_rows);			$this->set_paging_data($data, $query, $num_rows);									return $data;		}				/***********************************************************************/		/**************************** END OF EVENT LOGS *******************************/				/***********************************************************************/			}?>