<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_favorites_menu extends Fsadfzerrwez_home{		function Fsadfzerrwez_favorites_menu(){					parent::__construct();				$this->load->model("favorite_menu");					}				/***********************************************************************/		/**************************** START unitS *******************************/				/***********************************************************************/				function tracking(){			$this->check_login_ajax();						$id = $this->input->post('id');			$is_menu = $this->input->post('is_menu');			if ($id){				$this->favorite_menu->insert_or_update($id, $is_menu);			}		}			}?>