<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_organisation extends Fsadfzerrwez_home{		function Fsadfzerrwez_organisation(){					parent::__construct();					}				/***********************************************************************/		/**************************** START ORGANISATION CHART *******************************/				/***********************************************************************/				function organisation(){			$this->check_login();						$this->check_role(MODULE_ORGANISATION, MODULE_TYPE_VIEW);			$company_id = get_company_id();			$this->load->model('company');			$this->load->model('site');			$this->load->model('zone');						$data['sites'] = $this->site->get_all_sites($company_id);			$data['zones'] = $this->zone->get_all_ar_zones($company_id);			$data['company'] = $this->company->get_id($company_id);			$this->load_page('organisation', $this->lang->line('organisation'), $data);		}				/***********************************************************************/		/**************************** ORGANISATION CHART *******************************/				/***********************************************************************/			}?>