<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_sites extends Fsadfzerrwez_home{		function Fsadfzerrwez_sites(){					parent::__construct();				$this->load->model("site");					}				/***********************************************************************/		/**************************** START SITES *******************************/				/***********************************************************************/				function sites(){			$this->check_login();						$this->check_role(MODULE_SITES, MODULE_TYPE_VIEW);			$data = $this->get_sites();			$this->load_page('sites', $this->lang->line('sites'), $data);		}				function ajax_sites(){						$this->check_login_ajax();			$this->check_role_ajax(MODULE_SITES, MODULE_TYPE_VIEW);			$data = $this->get_sites();			$model['data'] = $data;			$model['controller_name'] = $this->controller_name;			$this->load->view($this->home_directory . "sites-table", $model);		}				private function get_sites(){			$data = $this->get_page_config();				$sort_by = $this->input->get('sort_by');			$ascending = $this->input->get('ascending');			$company_id = get_company_id();			$query = $this->site->get_sites($data['keywords'], $company_id, $data['page'], $data['page_size'], $sort_by, $ascending, $num_rows, get_language());			$this->set_paging_data($data, $query, $num_rows);									return $data;		}				function add_site($id = null){										$this->check_login();			$this->check_role(MODULE_SITES, MODULE_TYPE_VIEW);			$result = null;			$site = null;				$err_msg = '';			$company_id = get_company_id();			$data = array();
			$data['back-url'] = get_back_url(base_url("sites"), MODULE_SITES);			if (is_post()){									$result = false;				$site = $this->site->get_post_data();								$this->form_validation->set_rules('site_name', $this->lang->line('required-site-name'), 'trim|required');				if ($this->form_validation->run() == TRUE){					$site->company_id = $company_id;					//UPDATE CASE						if ($id){						$this->check_role(MODULE_SITES, MODULE_TYPE_UPDATE);												$result = $this->site->update($site, $company_id, $id);						if ($result){							$err_msg = get_saved_message('site', $site->site_name);							if ($this->input->post('save_and_continue') == false){																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}							else{							$err_msg = get_saved_fail_message('site', $site->site_name);						}					}					else{						$this->check_role(MODULE_SITES, MODULE_TYPE_ADD);												$result = $this->site->insert($site, get_user_id());												if ($result){								$err_msg = get_saved_message('site', $site->site_name);							if ($this->input->post('save_and_continue') == true){								//clear old data								$site->clear_data();							}							else{																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}						else{							$err_msg = get_saved_fail_message('site', $site->site_name);						}											}				}				else{					$err_msg = $this->lang->line('required-site');				}			}			else{								if ($id){										$site = $this->site->get_id($id);									}				else{										$site = new site();				}			}			$data['err_msg'] = $err_msg;			$data['result'] = $result;									$data['data'] = $site;			$data['id'] = $id;						$data['page'] = $this->input->get('page');			$data['page_size'] = $this->input->get('page_size');			$data['keywords'] = $this->input->get('keywords');			$title_page = $this->lang->line('site');			if (!$site){				$title_page = get_not_found_short();			}			$this->load_page('add-site', $title_page, $data);		}				function active_sites(){						$this->check_login_ajax();						$this->check_role_ajax(MODULE_SITES, MODULE_TYPE_UPDATE);			$active = $this->input->post('active');						$ids = $this->get_array_ids();									$this->site->active_in(get_company_id(), $active, $ids);		}				function delete_sites(){			$this->check_login_ajax();			$this->check_role_ajax(MODULE_SITES, MODULE_TYPE_DELETE);			$ids = $this->get_array_ids();			$this->site->delete_in(get_company_id(), $ids);		}		/***********************************************************************/		/**************************** END OF SITES *******************************/				/***********************************************************************/			}?>