<?php	$file = getcwd() . "/" . APPPATH . "controllers/MY_controller.php";			include $file;	class Fsadfzerrwez_types extends Fsadfzerrwez_home{		function Fsadfzerrwez_types(){					parent::__construct();				$this->load->model("type");					}				/***********************************************************************/		/**************************** START TYPES *******************************/				/***********************************************************************/				function types(){			$this->check_login();						$this->check_role(MODULE_TYPES, MODULE_TYPE_VIEW);			$data = $this->get_types();			$data['sub-types-url'] = get_back_url(base_url("sub-types"), MODULE_SUB_TYPES);			$this->load_page('types', $this->lang->line('types'), $data);		}				function ajax_types(){						$this->check_login_ajax();			$this->check_role_ajax(MODULE_TYPES, MODULE_TYPE_VIEW);			$data = $this->get_types();			$model['data'] = $data;			$model['controller_name'] = $this->controller_name;			$this->load->view($this->home_directory . "types-table", $model);		}				private function get_types(){			$data = $this->get_page_config();				$sort_by = $this->input->get('sort_by');			$ascending = $this->input->get('ascending');						$query = $this->type->get_types($data['keywords'], $data['page'], $data['page_size'], $sort_by, $ascending, $num_rows, get_language());			$this->set_paging_data($data, $query, $num_rows);									return $data;		}				function add_type($id = null){										$this->check_login();			$this->check_role(MODULE_TYPES, MODULE_TYPE_VIEW);			$result = null;			$type = null;				$err_msg = '';			$data = array();
			$data['back-url'] = get_back_url(base_url("types"), MODULE_TYPES);						if (is_post()){									$result = false;				$type = $this->type->get_post_data();								$this->form_validation->set_rules('type_name', $this->lang->line('required-type-name'), 'trim|required');				if ($this->form_validation->run() == TRUE){										//UPDATE CASE						if ($id){						$this->check_role(MODULE_TYPES, MODULE_TYPE_UPDATE);												$result = $this->type->update($type, $id);						if ($result){							$err_msg = get_saved_message('type', $type->type_name);							if ($this->input->post('save_and_continue') == false){																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}							else{							$err_msg = get_saved_fail_message('type', $type->type_name);						}					}					else{						$this->check_role(MODULE_TYPES, MODULE_TYPE_ADD);												$result = $this->type->insert($type, get_user_id());												if ($result){								$err_msg = get_saved_message('type', $type->type_name);							if ($this->input->post('save_and_continue') == true){								//clear old data								$type->clear_data();							}							else{																$this->set_flashdata($err_msg);								redirect($data['back-url']);							}						}						else{							$err_msg = get_saved_fail_message('type', $type->type_name);						}											}				}				else{					$err_msg = $this->lang->line('required-type');				}			}			else{								if ($id){										$type = $this->type->get_id($id);									}				else{										$type = new type();				}			}			$data['err_msg'] = $err_msg;			$data['result'] = $result;									$data['data'] = $type;			$data['id'] = $id;						$data['page'] = $this->input->get('page');			$data['page_size'] = $this->input->get('page_size');			$data['keywords'] = $this->input->get('keywords');			$title_page = $this->lang->line('type');			if (!$type){				$title_page = get_not_found_short();			}			$this->load_page('add-type', $title_page, $data);		}				function active_types(){						$this->check_login_ajax();						$this->check_role_ajax(MODULE_TYPES, MODULE_TYPE_UPDATE);			$active = $this->input->post('active');						$ids = $this->get_array_ids();									$this->type->active_in($active, $ids);		}				function delete_types(){			$this->check_login_ajax();			$this->check_role_ajax(MODULE_TYPES, MODULE_TYPE_DELETE);			$ids = $this->get_array_ids();			$this->type->delete_in($ids);		}		/***********************************************************************/		/**************************** END OF TYPES *******************************/				/***********************************************************************/			}?>