<h1 class='form-title'><?php echo $this->lang->line('home');?></h1><div id="home-summary">	<div class='zoom'>				<span id='year' class='active' ref='4'><?php echo $this->lang->line('this-year');?></span>		<span id='month' ref='3'><?php echo $this->lang->line('this-month');?></span>		<span id='week' ref='2'><?php echo $this->lang->line('this-week');?></span>		<span id='day' ref='1'><?php echo $this->lang->line('today');?></span>	</div>	<div id='summary' class='mt20'></div></div><script type='text/javascript'>	$(function(){		$('.zoom span').click(function(){			$('.zoom span').removeClass('active');			$(this).addClass('active');			load_data($(this).attr('ref'));		});		//load year data		load_data(4);		function load_data(date_type){			show_loading();			$.get(base_url + 'home-details/' + date_type, function(e){				$('#summary').html(e);				hide_loading();			});		}	});</script>